mapper.select:
select * from(select bnum ,b.cus_num ,service_num ,title ,content ,reg_date ,location ,count ,origin_name ,change_name ,b.status ,c.name ,c.changename profile from board b, customer c where b.cus_num = c.cus_num and bnum = #{bnum}) left join (select bnum, user_num, status lstatus, type from likes where user_num = #{userNum}
and type = #{type}
) using(bnum)

mapper.update:
update board 
   set service_num  = #{serviceNum}
       , title        = #{title}
       , content      = #{content}
       , reg_date     = sysdate
       , location     = #{location}
       , origin_name  = #{originName}
       , change_name  = #{changeName}
where bnum = #{bnum}

update board
   set status = 'N'
 where bnum = #{bnum}

update comments 
   set status ='N'
 where cnum = #{cnum}

mapper.select:
select count(*)
  from likes 
 where bnum = #{bnum} and user_num = #{userNum} and type = #{type}

mapper.update:
update likes
   set status = 'Y'
 where bnum = #{bnum} and user_num = #{userNum} and type = #{type}
