mapper.select:
select * from (select f.free_num, fr_content, call_time, reg_date, one_content, price, f.name, tel, career, gender, substr(location,4) location, c.name category, f.cate_num from freelancer_profile p, freelancer f, categories c where p.free_num = f.free_num and f.cate_num = c.cate_num and status = 'Y' and f.cate_num = #{cateNum}) a left join (select free_num, status from dibs where cus_num = #{cusNum}) b using(free_num) where free_num is not null
and price >= #{price1} and price <= #{price2}
and location = #{location}
order by reg_date desc

		select count(*)
		from freelancer
        join freelancer_profile
        using(free_num)
		where cate_num = #{cateNum}

			and price >= #{price1} and price <= #{price2}


			and substr(location,4) = #{location}

select count(*)
from dibs
where cus_num = #{cusNum}
and free_num = #{freeNum}

mapper.update:
update dibs
set status = 'N'
where cus_num = #{cusNum}
and free_num = #{freeNum}

update dibs
set status = 'Y'
where cus_num = #{cusNum}
and free_num = #{freeNum}
